exports.accept = function(req){
	console.log("进入接受");
	var fs = require('fs');
		var postdata = "";
		var localpath = "E:/web/blog/public/upload/a.txt";
		
		req.addListener("data",function(postchunk){
			postdata+=postchunk;
		});
		console.log("postchunk"+postdata);
		req.addListener("end",function(){
			fs.writeFile(localpath,postdata,function(err){
				if(err)
					console.log(err);
				console.log("成功接受文件\n");
			});
		});
}
     