var myutil = {};
myutil.getDate =  function (date) {
    date = date || '';
    var d = new Date(date);
    var ret = (d.getYear() + 1900) + '/'
        + (d.getMonth() + 1) + '/' + d.getDate() + ' '
        + d.getHours() + ':' + d.getMinutes() + ':'
        + d.getSeconds();
    return ret;
}
module.exports = myutil